// importing constant variables:
import { 
	CHANGE_SEARCH_FIELD,
	REQUEST_ROBOTS_PENDING,
	REQUEST_ROBOTS_SUCCESS,
	REQUEST_ROBOTS_FAILED
	} from './constants.js'

// action serSearchField returns an object:
export const setSearchField = (text) => ({
	type: CHANGE_SEARCH_FIELD,
	payload: text
})
